const StepSiteGenEditor = () => {
	const {
		setIsHeaderEnabled,
		setSidebarActiveView,
		setHeaderActiveView,
		setDrawerActiveView,
		updateAiPreviewSettings,
	} = useDispatch( nfdOnboardingStore );

	const { storedPreviewSettings } = useSelect( ( select ) => {
		return {
			storedPreviewSettings:
				select( nfdOnboardingStore ).getAiPreviewSettings(),
		};
	} );

	useEffect( () => {}, [ storedPreviewSettings ] );

	useEffect( () => {
		setIsHeaderEnabled( true );
		setSidebarActiveView( false );
		setHeaderActiveView( HEADER_SITEGEN );
		setDrawerActiveView( false );
		initGlobalStyles();
	}, [] );

	const initGlobalStyles = async () => {
		const globalStyles = await getGlobalStyles();
		const selectedGlobalStyle = globalStyles?.body[ 0 ];

		updateAiPreviewSettings(
			// eslint-disable-next-line react-hooks/rules-of-hooks
			useGlobalStylesOutput( selectedGlobalStyle, storedPreviewSettings )
		);
	};

	return (
		<CommonLayout
			isCentered
			className="nfd-onboarding-step--site-gen__editor"
		>
			{ /* <SiteGenPlaceholder heading={ 'Editing Previews' } /> */ }
			<LivePreview
				blockGrammer={ pattern }
				styling={ 'full' }
				viewportWidth={ 1300 }
				previewSettings={ storedPreviewSettings }
			/>
		</CommonLayout>
	);
};

export default StepSiteGenEditor;
